import { FC } from "react";

import { Box, Button, Container, Flex, HStack, useColorModeValue } from "@chakra-ui/react";

import { configs } from "shared/content/Content";
import { LogoType } from "shared/navbar/logo-type/LogoType";
import { bgDark, bgLight } from "theme";
import { onResumeOpen } from "utils/Functions";
import { AboutPageId, useScroll, WorkPageId, HomeId } from "utils/useScroll";
import { MenuDrawer } from "./drawer/Drawer";
import { ColorModeButton } from "shared/color-mode-button/ColorModeButton";

export const open = (link: string) => window.open(link, "_blank");
export const Navbar: FC = () => {
    const bg = useColorModeValue(bgLight, bgDark);

    const navItemColor = useColorModeValue("gray.800", "white");
    const currentPage = useScroll();

    const toSection = (section: string, offset=75) => {
        const element = document.getElementById(section)
        if (element) {
            const elementPosition = element.getBoundingClientRect().top + window.scrollY;
            const targetPosition = elementPosition - offset;
            window.scrollTo({ top: targetPosition, behavior: "smooth" });
        }
    };

    // const toSection = (section: string) => {
    //     document.getElementById(section)?.scrollIntoView({ behavior: "smooth" });
    // };

    return (
        <Box bg={bg} position="fixed" top="0" w="100%" left="50%" transform="translate(-50%)" zIndex="10" borderBottomWidth='1px'>
            <Container py="4" px="4" mt="5px" mb="5px" data-aos="fade-down" data-aos-duration="500" data-aos-delay="100">
                <Flex justifyContent="space-between" alignItems="center">
                    <LogoType text={configs.common.logoType} /> 
                    <Flex alignItems="center" display={{ base: "none", md: "flex" }}>
                        <HStack spacing="8" mr="6">

                        <Button
                                variant="link"
                                color={navItemColor}
                                textDecoration={currentPage === HomeId ? "underline" : "none"}
                                textDecorationThickness="2px"
                                textDecorationColor="primary.500"
                                onClick={() => toSection(HomeId)}
                                data-aos="fade"
                                data-aos-delay="300"
                            >
                                Profile
                            </Button>


                            <Button
                                variant="link"
                                color={navItemColor}
                                textDecoration={currentPage === AboutPageId ? "underline" : "none"}
                                textDecorationThickness="2px"
                                textDecorationColor="primary.500"
                                onClick={() => toSection(AboutPageId)}
                                data-aos="fade"
                                data-aos-delay="300"
                            >
                                Publication
                            </Button>
                            <Button
                                variant="link"
                                color={navItemColor}
                                textDecoration="underline"
                                textDecorationThickness="2px"
                                textDecorationColor={currentPage === WorkPageId ? "primary.500" : "transparent"}
                                onClick={() => toSection(WorkPageId)}
                                data-aos="fade"
                                data-aos-delay="200"
                            >
                                Project
                            </Button>
                            <Button variant="link" onClick={() => open("https://scholar.google.co.kr/citations?user=KzrZ7zAAAAAJ&hl")} data-aos="fade" data-aos-delay="400">
                                Google Scholar
                            </Button>
                            {/* <Button variant="link" onClick={onResumeOpen} data-aos="fade" data-aos-delay="400">
                                Resume
                            </Button> */}
                        </HStack>
                        <ColorModeButton />
                    </Flex>

                    <MenuDrawer
                        currentPage={currentPage}
                        onSectionClick={toSection}
                        display={{ base: "block", md: "none" }}
                    />
                </Flex>
            </Container>
        </Box>
    );
};
